// FIXED VERSION - Correct team names to match Intercom
// ------------------------------------

// CONFIG - 3 TEAMS (EXACT NAMES FROM INTERCOM)
const TEAM_PSTF_NAME = "Pro Solutions Task Force";  // FIXED: Added 's' to match Intercom
const TEAM_DEPENDENCY_NAME = "Ticket Dependencies";
const TEAM_REVERSAL_NAME = "CEx Reversal";

// HANDLER ALLOWLIST - Real resolvers only (NO BOTS)
const ALLOWED_AGENTS = [
  "Jane Megan",
  "Fredy Martinez",
  "Sandro Jerome",
  "Jack Carter",
  "Tyson Mayne",
  "Willium Grace",
  "Prina Isabella",
  "Sammy Sage",
  "Ricky Ron",
  "Danny Archer",
  "Ben Clark",
  "Zoe Castillo",
  "Sam Fisher",
  "Aiden Garcia"
];

// ------------------------------------
// Helpers
// ------------------------------------
function stripHtml(html) {
  if (!html) return "";
  return String(html)
    .replace(/<br\s*\/?>/gi, "\n")
    .replace(/<\/p>/gi, "\n")
    .replace(/<[^>]+>/g, " ")
    .replace(/\s+/g, " ")
    .trim();
}

function toISODate(unixSeconds) {
  const n = (typeof unixSeconds === "number") ? unixSeconds : parseInt(unixSeconds, 10);
  if (!n || Number.isNaN(n)) return "";
  return new Date(n * 1000).toISOString().slice(0, 10); 
}

function norm(s) { return (s || "").toString().trim().toLowerCase(); }

function getAuthor(part) {
  const a = part?.author || part?.created_by || part?.creator || {};
  return {
    id: a?.id ? String(a.id) : "",
    name: a?.name || "",
    email: a?.email || "",
    type: a?.type || ""
  };
}

// Allowlist check
const ALLOWED_SET = new Set(ALLOWED_AGENTS.map(n => norm(n)));
function isAllowedAgent(name) {
  if (!name) return false;
  return ALLOWED_SET.has(norm(name));
}

// ------------------------------------
// Data Setup
// ------------------------------------
const ticket = $json || {};
const teamMap = $('Build Team Map4').first().json || {};
const nameToId = teamMap.nameToId || {};
const idToName = teamMap.idToName || {};

const pstfTeamId = nameToId[norm(TEAM_PSTF_NAME)];
const depTeamId  = nameToId[norm(TEAM_DEPENDENCY_NAME)];
const revTeamId  = nameToId[norm(TEAM_REVERSAL_NAME)];

const targetTeamIds = [pstfTeamId, depTeamId, revTeamId].filter(Boolean);
const currentTeamId = ticket.team_assignee_id ? String(ticket.team_assignee_id) : null;
const currentTeamName = currentTeamId ? (idToName[currentTeamId] || "Unknown") : "None";

// Metadata
const ticketDisplayId = String(ticket.ticket_id || ticket.id || "");
const ticketState = norm(ticket.ticket_state || ticket.state || "");
const ticketStateLabel = ticket.ticket_state_internal_label || ticket.ticket_state_external_label || ticket.ticket_state || "";

// Resolution check
const isResolved = (ticketState === "resolved" || ticketState === "closed");

// Team check
const isInTargetTeam = (currentTeamId && targetTeamIds.includes(currentTeamId));

let issueCategory = "";
if (ticket.ticket_type && ticket.ticket_type.name) {
  issueCategory = ticket.ticket_type.name;
} else if (ticket.custom_attributes && ticket.custom_attributes.ticket_type) {
  issueCategory = ticket.custom_attributes.ticket_type;
}
if (!issueCategory) {
  const titleRaw = ticket?.ticket_attributes?._default_title_ || ticket?.ticket_attributes?._default_title || ticket?.title || ticket?.subject || "";
  issueCategory = stripHtml(titleRaw);
}

const contact = (ticket?.contacts?.contacts && ticket.contacts.contacts[0]) || (Array.isArray(ticket?.contacts) ? ticket.contacts[0] : null);
const contactName = contact?.name || "";
const contactEmail = contact?.email || "";

let parts = ticket.ticket_parts || [];
if (!Array.isArray(parts)) {
  const tp = ticket.ticket_parts || {};
  parts = Array.isArray(tp.ticket_parts) ? tp.ticket_parts : (Array.isArray(tp.data) ? tp.data : []);
}
parts.sort((a, b) => (a?.created_at || 0) - (b?.created_at || 0));

// Creator
let creatorName = "";
let creatorEmail = "";
const firstAdminPart = parts.find(p => getAuthor(p).type === "admin");
if (firstAdminPart) {
  const au = getAuthor(firstAdminPart);
  creatorName = au.name;
  creatorEmail = au.email;
} else {
  creatorName = contactName;
  creatorEmail = contactEmail;
}

let lastAdminNoteText = "";

// Collect all potential handlers
let allPotentialHandlers = [];
const assignee = ticket.assignee || {};
if (assignee.type === "admin" && assignee.name) {
  allPotentialHandlers.push(assignee.name);
}

for (const p of parts) {
  const au = getAuthor(p);
  const body = stripHtml(p?.body || p?.comment || p?.note || "");
  const partType = norm(p?.part_type || p?.event_type || p?.type || "");
  const isAdmin = norm(au.type) === "admin";
  
  if (isAdmin && body) lastAdminNoteText = body;
  
  if (partType === "assignment") {
    const assignedTo = p.assigned_to || {};
    if (assignedTo.type === "admin" && assignedTo.name) {
      allPotentialHandlers.push(assignedTo.name);
    }
  }
  
  if (partType === "close" && au.name) {
    allPotentialHandlers.push(au.name);
  }
}

// ---------------------------------------------------------
// Handler Determination - ONLY FROM ALLOWLIST
// ---------------------------------------------------------
let handlerName = "";
for (const candidate of allPotentialHandlers) {
  if (isAllowedAgent(candidate)) {
    handlerName = candidate;
    break;
  }
}

if (!handlerName) {
  for (const p of parts) {
    const au = getAuthor(p);
    if (au.type === "admin" && isAllowedAgent(au.name)) {
      handlerName = au.name;
      break;
    }
  }
}

// ---------------------------------------------------------
// FILTER: Resolved + In Target Team
// ---------------------------------------------------------
const qualifies = isResolved && isInTargetTeam;

// Debug info
let debugInfo = `State: ${ticketState}, Team: ${currentTeamName}, InTarget: ${isInTargetTeam}, TargetIDs: [${targetTeamIds.join(',')}], CurrentID: ${currentTeamId}`;

// Output
const date = toISODate(ticket.updated_at || ticket.created_at);
const uniqueId = `${date}|${ticketDisplayId}`;

return [{
  json: {
    "_filter_passed": qualifies,
    "_debug_info": debugInfo,
    "_potential_handlers": allPotentialHandlers.join(", "),
    "Unique ID": uniqueId,
    "Date": date,
    "Ticket ID": ticketDisplayId,
    "Ticket Creator Agent Name": creatorName,
    "Ticket Handler Agent Name": handlerName,
    "Ticket Status": ticketStateLabel,
    "Description/Last Ticket Note": lastAdminNoteText,
    "Issue Category": issueCategory, 
    "Dependency Cleared": "",
    "Dependency No Update": "",
    "Forwarded?": "", 
    "Forwarded To": "",
    "Reversal Ticket?": "",
    "Invalid?": "",
    "Reversal Reason": "",
    "Ticket Creator's Email": creatorEmail,
    "Email Communication?": ""
  }
}];

// Gate: do nothing until 2025-06-01 (UTC)
// NOTE: Workflow static data only persists when the workflow is ACTIVE and triggered by Schedule/Webhook.

const START_UNIX = 1748736000; // 2025-06-01 00:00:00 UTC
const nowUnix = Math.floor(Date.now() / 1000);

if (nowUnix < START_UNIX) {
  // Before start date -> end workflow quietly
  return [];
}

// Initialize incremental sync state
const sd = $getWorkflowStaticData('global');
if (typeof sd.last_sync !== 'number') sd.last_sync = START_UNIX;

return [{
  json: {
    start_unix: START_UNIX,
    now_unix: nowUnix,
    last_sync: sd.last_sync
  }
}];

// Build team maps: id->name and name(lower)->id

function ensureObj(x){
  if (!x) return {};
  if (typeof x === 'string'){
    try { return JSON.parse(x); } catch { return {}; }
  }
  return x;
}

const resp = ensureObj($json);
const teams = resp.teams || resp.data || [];

const idToName = {};
const nameToId = {};

for (const t of teams) {
  if (!t || !t.id) continue;
  const name = (t.name || '').trim();
  idToName[t.id] = name;
  if (name) nameToId[name.toLowerCase()] = t.id;
}

return [{
  json: {
    idToName,
    nameToId
  }
}];

// Build the Intercom /tickets/search request body.
// OPTIMIZED: Filter by TEAM + RESOLVED STATUS at API level

const START_UNIX = 1704067200; // 2024-01-01 00:00:00 UTC
const sd = $getWorkflowStaticData('global');

sd.run_max_updated_at = Math.floor(Date.now() / 1000);

// Get Team Map
const teamMap = $('Build Team Map4').first().json || {};
const nameToId = teamMap.nameToId || {};

function norm(s) { return (s || "").toString().trim().toLowerCase(); }

// CORRECT TEAM NAMES
const TEAM_PSTF_NAME = "Pro Solutions Task Force";
const TEAM_DEPENDENCY_NAME = "Ticket Dependencies";
const TEAM_REVERSAL_NAME = "CEx Reversal";

const pstfTeamId = nameToId[norm(TEAM_PSTF_NAME)];
const depTeamId  = nameToId[norm(TEAM_DEPENDENCY_NAME)];
const revTeamId  = nameToId[norm(TEAM_REVERSAL_NAME)];

const targetTeamIds = [pstfTeamId, depTeamId, revTeamId].filter(Boolean);

// Build team filter conditions
const teamConditions = targetTeamIds.map(id => ({
  field: "team_assignee_id",
  operator: "=",
  value: id
}));

// Build state filter for resolved tickets
const stateConditions = [
  { field: "state", operator: "=", value: "resolved" },
  { field: "state", operator: "=", value: "closed" }
];

// Construct final query:
// (updated_at > START_UNIX) AND (team1 OR team2 OR team3) AND (resolved OR closed)
const queryConditions = [
  { field: "updated_at", operator: ">", value: START_UNIX }
];

// Add team filter (OR across teams)
if (teamConditions.length === 1) {
  queryConditions.push(teamConditions[0]);
} else if (teamConditions.length > 1) {
  queryConditions.push({
    operator: "OR",
    value: teamConditions
  });
}

// Add state filter (resolved OR closed)
queryConditions.push({
  operator: "OR",
  value: stateConditions
});

return [{
  json: {
    body: {
      query: {
        operator: "AND",
        value: queryConditions
      },
      pagination: {
        per_page: 150
      }
    },
    meta: {
      start_unix: START_UNIX,
      mode: "TEAM_AND_STATE_FILTERED",
      target_teams: targetTeamIds,
      states: ["resolved", "closed"]
    }
  }
}];

// Pagination + watermark update (workflow static data)
//
// 1) Updates sd.run_max_updated_at with the max updated_at seen so far.
// 2) Prepares the next request body with pagination.starting_after.
// 3) On the LAST page, commits sd.last_sync = sd.run_max_updated_at.

function clone(o) {
  return JSON.parse(JSON.stringify(o));
}

function ensureObj(x) {
  if (!x) return {};
  if (typeof x === 'string') {
    try { return JSON.parse(x); } catch { return {}; }
  }
  return x;
}

const resp = ensureObj($json);
const pageNow = resp?.pages?.page ?? 1;
const nextCursor = resp?.pages?.next?.starting_after ?? null;

// Update max updated_at seen this run
const sd = $getWorkflowStaticData('global');
let maxSeen = (typeof sd.run_max_updated_at === 'number') ? sd.run_max_updated_at : 0;

const tickets = Array.isArray(resp?.tickets) ? resp.tickets : [];
for (const t of tickets) {
  const ua = (typeof t?.updated_at === 'number') ? t.updated_at : parseInt(t?.updated_at, 10);
  if (!Number.isNaN(ua)) maxSeen = Math.max(maxSeen, ua);
}

sd.run_max_updated_at = maxSeen;

// Clone the original page-1 body so query filters stay the same
const baseBody = clone($("Build HTTP Body4").first().json.body);
baseBody.pagination = baseBody.pagination || {};
baseBody.pagination.per_page = 150;
if (nextCursor) baseBody.pagination.starting_after = nextCursor;
else delete baseBody.pagination.starting_after;

// Safety cap to avoid infinite loops
const MAX_PAGES = 2000;
const hasNext = Boolean(nextCursor) && pageNow < MAX_PAGES;

// On last page: commit watermark
if (!hasNext) {
  const START_UNIX = 1748736000;
  const current = (typeof sd.last_sync === 'number') ? sd.last_sync : START_UNIX;
  sd.last_sync = Math.max(current, sd.run_max_updated_at || current);
  delete sd.run_max_updated_at;
}

return [{
  json: {
    body: baseBody,
    has_next: hasNext,
    page_now: pageNow
  }
}];
